.todo-form {
  display: flex;
  margin: 24px;
  margin-bottom: 32px;
  justify-content: center;
}

.form-label {
  margin-right: 16px;
}

.form-input {
  display: block;
  margin: 8px 0;
  padding: 8px;
  border-radius: 3px;
  border: 0.5px solid rgba(0, 0, 0, 0.15);
}

.form-input:focus {
  border: 0.5px solid #42b983;
  outline: 0;
  box-shadow: none;
}

.todo-form-btn {
  /* clear button styling */
  background: none;
  outline: none;
  border: none;
  padding: 0;

  /* custom styling */
  background-color: white;
  border: 1px solid #42b983;
  margin-left: 8px;
  margin-bottom: 8px;
  border-radius: 50%;
  width: 32px;
  height: 32px;
  color: #42b983;
  font-size: 1.2em;
  /* font-weight: bold; */
  align-self: flex-end;
}

.cancel-button {
  font-size: 0.83em;
}

todo-form-btn:active {
  background-color: #42b983;
  color: white;
}
